    // Sorted data
    Dictionary<string, Action> selectedSortDictionary;

    private Pair[] pairs;

    // Get Data
    protected override async Task OnInitializedAsync()
    {
        pairs = await Http.GetFromJsonAsync<Pair[]>("https://api.n.exchange/en/api/v1/pair/");
        // Storing sorted data
        selectedSortDictionary = new Dictionary<string, Action>
        {
            ["pairA"] = () => pairs = pairs.OrderBy(s => s.name).ToArray(),
            ["pairD"] = () => pairs = pairs.OrderByDescending(s => s.name).ToArray(),
            ["askingA"] = () => pairs = pairs.OrderBy(s => s.fee_ask).ToArray(),
            ["askingD"] = () => pairs = pairs.OrderByDescending(s => s.fee_ask).ToArray(),
        };
    }

